mapper.select:
select 

from game


license_id = #{param.licenseId}


and issue = #{param.issue}


and state = #{param.state}


order by issue DESC
limit #{pagination.firstResult},#{pagination.pageSize}

select count(id)
from game


license_id = #{param.licenseId}


and issue = #{param.issue}


and state = #{param.state}

mapper.update:
update game
set state = #{state,jdbcType=INTEGER}
where id = #{id,jdbcType=INTEGER}

mapper.select:
