mapper.select:
select store_drow_cz_yp.id, store_drow_cz_yp.store_id, store.store_name, store_drow_cz_yp.money, store_drow_cz_yp.bank_account, store_drow_cz_yp.bank_name, store_drow_cz_yp.bank_user_name, store_drow_cz_yp.operator_id, store_drow_cz_yp.operator_name, store_drow_cz_yp.create_time, store_drow_cz_yp.operator_time, store_drow_cz_yp.state, store_drow_cz_yp.comments, store_drow_cz_yp.user_id, store_drow_cz_yp.reserved1, store_drow_cz_yp.reserved2, store_drow_cz_yp.reserved3, store_drow_cz_yp.reserved4, store_drow_cz_yp.reserved5, store_user.user_name from store_drow_cz_yp left join store on store_drow_cz_yp.store_id = store.store_id left join store_user on store_user.user_id = store_drow_cz_yp.user_id
store_drow_cz_yp.state = #{param.state}
and (store_drow_cz_yp.create_time between CONCAT(#{param.createTimeBegin}," 00:00:00") and CONCAT(#{param.createTimeEnd}," 23:59:59"))
and store_drow_cz_yp.create_time > CONCAT(#{param.createTimeBegin}," 00:00:00")
and store_drow_cz_yp.create_time < CONCAT(#{param.createTimeEnd}," 23:59:59")
order by store_drow_cz_yp.id desc limit #{pagination.firstResult},#{pagination.pageSize}

select , (select user_name from store_user where user_id = store_drow_cz_yp.user_id) user_name from store_drow_cz_yp
store_id = #{param.storeId}
and state = #{param.state}
and state in #{item}
and create_time between (CONCAT(#{param.createTimeBegin}," 00:00:00") and CONCAT(#{param.createTimeEnd}," 23:59:59"))
and create_time > CONCAT(#{param.createTimeBegin}," 00:00:00")
and create_time < CONCAT(#{param.createTimeEnd}," 23:59:59")
and operator_time between (CONCAT(#{param.operatorTimeBegin}," 00:00:00") and CONCAT(#{param.operatorTimeEnd}," 23:59:59"))
and operator_time > CONCAT(#{param.operatorTimeBegin}," 00:00:00")
and operator_time < CONCAT(#{param.operatorTimeEnd}," 23:59:59")
order by create_time desc,operator_time desc limit #{pagination.firstResult},#{pagination.pageSize}

select count(*) from store_drow_cz_yp
store_id = #{param.storeId}
and state = #{param.state}
and state in #{item}
and create_time between (CONCAT(#{param.createTimeBegin}," 00:00:00") and CONCAT(#{param.createTimeEnd}," 23:59:59"))
and create_time > CONCAT(#{param.createTimeBegin}," 00:00:00")
and create_time < CONCAT(#{param.createTimeEnd}," 23:59:59")
and operator_time between (CONCAT(#{param.operatorTimeBegin}," 00:00:00") and CONCAT(#{param.operatorTimeEnd}," 23:59:59"))
and operator_time > CONCAT(#{param.operatorTimeBegin}," 00:00:00")
and operator_time < CONCAT(#{param.operatorTimeEnd}," 23:59:59")
