select store_money_cz_log.id, store_money_cz_log.store_id,store.store_name, store_money_cz_log.money, store_money_cz_log.balance, store_money_cz_log.type, store_money_cz_log.category, store_money_cz_log.category_detail, store_money_cz_log.relation_id, store_money_cz_log.state, store_money_cz_log.operator, store_money_cz_log.commont, store_money_cz_log.create_time, store_money_cz_log.back_time, store_money_cz_log.reserved1, store_money_cz_log.reserved2, store_money_cz_log.reserved3, store_money_cz_log.reserved4, store_money_cz_log.reserved5, store_money_cz_log.reserved6, store_money_cz_log.reserved7, store_money_cz_log.reserved8, store_money_cz_log.reserved9, store_money_cz_log.reserved10 from store_money_cz_log left join store on store.store_id = store_money_cz_log.store_id
store_money_cz_log.store_id = #{param.storeId} and store_money_cz_log.category = #{param.category} and store_money_cz_log.category_detail = #{param.categoryDetail}
and (store_money_cz_log.create_time between CONCAT(#{param.createTimeBegin}," 00:00:00") and CONCAT(#{param.createTimeEnd}," 23:59:59"))
and store_money_cz_log.create_time > CONCAT(#{param.createTimeBegin}," 00:00:00")
and store_money_cz_log.create_time < CONCAT(#{param.createTimeEnd}," 23:59:59")
order by store_money_cz_log.id DESC limit #{pagination.firstResult},#{pagination.pageSize}

