mapper.select:
select  from store_receiv

	store_id = #{param.storeId}
	 and type = #{param.type}
	 and state = #{param.state}
	 and usefor = #{param.usefor}


limit #{pagination.firstResult},#{pagination.pageSize}

select count(*) from store_receiv

	store_id = #{param.storeId}
	 and type = #{param.type}
	 and state = #{param.state}
	 and usefor = #{param.usefor}
